import React, { useState } from 'react';
import emailjs from '@emailjs/browser';

const Home: React.FC = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const [isSent, setIsSent] = useState(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    emailjs
      .send(
        'service_m9aqnaf', // Replace with your EmailJS Service ID
        'template_cz1q22p', // Replace with your EmailJS Template ID
        formData,
        'MGncN7kASSIMdB4hu' // Replace with your EmailJS Public Key
      )
      .then(
        () => {
          setIsSent(true);
          setFormData({ name: '', email: '', message: '' }); // Reset form
        },
        (error: any) => {
          console.error('Failed to send email:', error);
        }
      );
  };

  return (
    <div className="container mx-auto text-center py-10">
      {/* Introduction Section */}
      <div className="flex flex-col items-center">
        <img
          src="/image/musa.jpg" // Replace with the actual path to your image
          alt="Musa Kerempe"
          className="w-40 h-40 rounded-full mb-4 shadow-lg"
        />
        <h1 className="text-4xl font-bold mb-4 text-blue-600 dark:text-blue-400">
          Hi, I'm Musa Kerempe
        </h1>
        <p className="text-lg mb-6 text-gray-700 dark:text-gray-300">
          I'm a passionate software and web developer dedicated to creating innovative and user-friendly digital solutions. I enjoy solving complex problems and delivering high-quality applications that make a difference.
        </p>
      </div>

      {/* Contact Section */}
      <div className="mt-16 bg-gray-800 text-white py-8 rounded-lg shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-4">Contact Me</h2>
        {isSent && (
          <p className="text-center text-green-500 mb-4">Your message has been sent successfully!</p>
        )}
        <form onSubmit={handleSubmit} className="max-w-md mx-auto">
          <div className="mb-4">
            <label htmlFor="name" className="block text-sm font-medium mb-2">
              Name
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-900"
              placeholder="Your Name"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="email" className="block text-sm font-medium mb-2">
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-900"
              placeholder="Your Email"
              required
            />
          </div>
          <div className="mb-4">
            <label htmlFor="message" className="block text-sm font-medium mb-2">
              Message
            </label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleChange}
              rows={4}
              className="w-full px-3 py-2 border border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-900"
              placeholder="Your Message"
              required
            ></textarea>
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Send Message
          </button>
        </form>
      </div>
    </div>
  );
};

export default Home;
